mapper.update:
UPDATE ew_lexicon_4500
phoneticSigns_uk = #{phoneticSigns_uk}, phoneticSigns_us = #{phoneticSigns_us}, noun = #{noun}, verb = #{verb}, adjective = #{adjective}, pluralForm = #{pluralForm}, resentParticiple = #{resentParticiple}, pastTense = #{pastTense}, pastTense = #{pastTense}
WHERE elId = #{elId}

mapper.select:
SELECT el.elId,el.word,el.phoneticSigns_uk,el.phoneticSigns_us,el.noun, el.verb,el.adjective,el.pluralForm,el.resentParticiple,el.pastTense, ef.efId FROM ew_favorites ef LEFT JOIN ew_lexicon_4500 el ON el.elId = ef.elId WHERE ef.userId = #{userId}
el.word LIKE '%${search}%' OR el.noun LIKE '%${search}%' OR el.verb LIKE '%${search}%' OR el.adjective LIKE '%${search}%'
ORDER BY ef.createTime DESC
